WHERE nome IN (SELECT nome FROM Medicos M2 WHERE M1.nome = nome AND M1.crm > crm)
ORDER BY nome;

d) SELECT nome, M1.crm, M2.crm FROM Medicos M1 NATURAL JOIN Medicos M2 WHERE
M1.crm > M2.crm
ORDER BY nome;

e) SELECT * FROM Medicos as M1, Medicos as M2
WHERE M1.nome LIKE M2.nome AND crm1 > crm2
GROUP BY M1.nome;

